// if 수행문장의 범위

public class 제어문_조건문2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num = 20;
		int b = 100;
		if(num%2==0)
		{
			System.out.println("b는 " + b + "입니다");
			System.out.println(num + "는(은) 짝수입니다");
			System.out.println("프로그램 종료");
			b++;
		}
		System.out.println(b);
		
	}

}
